(509, '509. Które zapytanie SQL dla tabeli pracownicy utworzonej według schematu: id, imie,\n            nazwisko, plec, zarobek, obliczy osobno średni zarobek kobiet i średni zarobek mężczyzn?', NULL, 'A. SELECT AVG(zarobek) FROM pracownicy GROUP BY plec;', 'B. SELECT AVG(zarobek) FROM pracownicy AS sredni_zarobek;', 'C. SELECT AVG(zarobek) FROM pracownicy WHERE plec=\'k\' AND plec=\'m\';', 'D. SELECT AVG(zarobek) FROM pracownicy GROUP BY plec HAVING plec=\'k\' AND\n            plec=\'m\';', 'A'),
(510, '510. Które polecenie SQL zamieni w tabeli tab w kolumnie kol wartość Ania na Zosia?', NULL, 'A. UPDATE tab SET kol=\'Zosia\' WHERE kol=\'Ania\';', 'B. UPDATE tab SET kol=\'Ania\' WHERE kol=\'Zosia\';', 'C. ALTER TABLE tab CHANGE kol=\'Zosia\' kol=\'Ania\';', 'D. ALTER TABLE tab CHANGE kol=\'Ania\' kol=\'Zosia\';', 'A'),
